package com.graduation.project.user.mapper;import com.graduation.project.common.entity.Annoucement;import com.graduation.project.user.dto.AnnoucementResponse;import java.util.List;import org.mapstruct.*;import org.mapstruct.Mapper;@Mapper(componentModel = "spring")public interface AnnoucementMapper {  @Mapping(source = "createdBy.fullName", target = "createdByFullName")  @Mapping(source = "modifiedBy.fullName", target = "modifiedByFullName")  @Mapping(source = "targets", target = "annoucementTargetResponses")  AnnoucementResponse toResponse(Annoucement entity);  List<AnnoucementResponse> toResponseList(List<Annoucement> annoucements);}