package com.graduation.project.user.mapper;import com.graduation.project.common.entity.Announcement;import com.graduation.project.user.dto.AnnouncementResponse;import java.util.List;import org.mapstruct.*;import org.mapstruct.Mapper;@Mapper(componentModel = "spring")public interface AnnouncementMapper {  @Mapping(source = "createdBy.fullName", target = "createdByFullName")  @Mapping(source = "modifiedBy.fullName", target = "modifiedByFullName")  @Mapping(source = "targets", target = "announcementTargetResponses")  AnnouncementResponse toResponse(Announcement entity);  List<AnnouncementResponse> toResponseList(List<Announcement> announcements);}